mapper.update:
update
  t_user

     ,u_name = #{uName}
     ,u_tel = #{uTel}
     ,b_id = #{bId}
     ,address = #{address}
     ,account_balance = #{accountBalance}
     ,enprNo = #{enprNo}

where
  u_id = #{uId}
  and is_delete = 0

mapper.select:
select
  *
from
  t_user
where
  u_name = #{uName}
  and u_tel = #{tel}
  and enprNo = #{enprNo}
  and is_delete = 0
